// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;
import "./ShamirSecretSharing.sol";

contract FileStorageSSS {
    struct File {
        string name;
        uint8 threshold; // Threshold for Shamir's Secret Sharing (4)
        uint8 numShares; // Number of shares for Shamir's Secret Sharing (4)
        uint256[] contentShares; // Shamir's Secret Sharing shares of content (4 shares)
        address owner;
    }


    File[] files;
    mapping(address => uint[]) private ownerIndexes;
    uint256 public fileId;

    uint8 constant public THRESHOLD = 2; // Minimum number of shares to reconstruct
    uint8 constant public TOTAL_SHARES = 3; // Total number of shares to generate

    // Initialize the Shamir's Secret Sharing contract
    ShamirSecretSharing public sss = new ShamirSecretSharing();

    constructor() {
        fileId = 0;
    }

    function getOwnerFiles() public view returns (File[] memory) {
        uint256 fileCount = ownerIndexes[msg.sender].length;

        File[] memory ownerFiles = new File[](fileCount);

        for (uint256 i = 0; i < fileCount; i++) {
            ownerFiles[i] = files[ownerIndexes[msg.sender][i]];
        }

        return ownerFiles;
    }

    function addFile(string memory name, string memory content) public {
        uint256 index = files.length;
        uint256 secret = uint256(keccak256(abi.encodePacked(content)));
        uint256[] memory contentShares = sss.splitSecret(secret, THRESHOLD, TOTAL_SHARES); // Split into 4 shares, each stored on a separate node
        files.push(File(name, THRESHOLD, TOTAL_SHARES, contentShares, msg.sender));
        ownerIndexes[msg.sender].push(index);
        fileId++;
    }

    function getFile(uint256 _fileId) public view returns (string memory name, string memory content) {
        require(msg.sender == files[_fileId].owner, "You are not authorized to view this file.");
        uint256 secret = sss.combineShares(files[_fileId].contentShares, THRESHOLD);
        return (files[_fileId].name, bytes32ToString(bytes32(secret)));
    }

    function updateFile(uint256 index, string memory name, string memory content) public {
        require(index < files.length, "Invalid file index");
        require(files[index].owner == msg.sender, "Not authorized to update file");
        uint256 secret = uint256(keccak256(abi.encodePacked(content)));
        uint256[] memory contentShares = sss.splitSecret(secret, THRESHOLD, TOTAL_SHARES); // Split into 4 shares, each stored on a separate node
        files[index] = File(name, THRESHOLD, TOTAL_SHARES, contentShares, msg.sender);
    }

    // Utility function to convert bytes32 to a string
    function bytes32ToString(bytes32 _bytes32) internal pure returns (string memory) {
        uint8 i = 0;
        while (i < 32 && _bytes32[i] != 0) {
            i++;
        }
        bytes memory bytesArray = new bytes(i);
        for (i = 0; i < 32 && _bytes32[i] != 0; i++) {
            bytesArray[i] = _bytes32[i];
        }
        return string(bytesArray);
    }
}
